create table ticket_filters (version integer, created timestamp(6) with time zone not null, id bigint generated by default as identity, modified timestamp(6) with time zone, name varchar(2048) not null unique, created_by varchar(255), modified_by varchar(255), filter json not null, primary key (id));
create table ticket_filters_aud (rev integer not null, revtype tinyint, id bigint not null, name varchar(2048), filter json, primary key (rev, id));